import globimport osimport jsondef get_files(directory):    # Use '**' to search recursively and '*' to match all files    pattern = os.path.join(directory, '**', '*')    return [f for f in glob.glob(pattern, recursive=True) if os.path.isfile(f)]def get_files_from_directories(directories):    files = []    for directory in directories:        files += get_files(directory)    return filesdef extract_file_list_from_paths(path_list):    file_list = []    for file_name in path_list:        if os.path.isdir(file_name):            extracted_video_files = get_files(file_name)            file_list += extracted_video_files        elif os.path.isfile(file_name):            file_list.append(file_name)    return file_listdef save_json(data, file_path):    directory = os.path.dirname(file_path)    if not os.path.exists(directory):        os.makedirs(directory)    with open(file_path, 'w', encoding='utf-8') as file:        file.write(data)def load_json(file_path):    directory = os.path.dirname(file_path)    if not os.path.exists(directory):        return None    with open(file_path, 'r', encoding='utf-8') as file:        return json.loads(file.read())def get_script_env_folder_path():    return os.getenv('LOCALAPPDATA') + r"\obs-studio\scripts\Video Randomizer"